mapper.update:
UPDATE flash_bucket
SET modified_time = now(),
    status        = #{status}
WHERE item_id = #{itemId}

UPDATE flash_bucket
SET modified_time           = now(),
    available_stocks_amount = available_stocks_amount - #{quantity}
where item_id = #{itemId}
  and serial_no = #{serialNo}
  and available_stocks_amount  >=   #{quantity}
  and status = 1

UPDATE flash_bucket
SET modified_time           = now(),
    available_stocks_amount = available_stocks_amount + #{quantity}
where item_id = #{itemId}
  and serial_no = #{serialNo}
  and (available_stocks_amount + #{quantity})  <=  total_stocks_amount
  and status = 1
