mapper.update:
UPDATE sale_bucket
SET modified_time = now(),
    status        = #{status}
WHERE item_id = #{itemId}

UPDATE sale_bucket
SET modified_time           = now(),
    available_stock_amount = available_stock_amount - #{quantity}
where item_id = #{itemId}
  and serial_no = #{serialNo}
  and available_stock_amount  >=   #{quantity}
  and status = 1

UPDATE sale_bucket
SET modified_time           = now(),
    available_stock_amount = available_stock_amount + #{quantity}
where item_id = #{itemId}
  and serial_no = #{serialNo}
  and (available_stock_amount + #{quantity})  <=  total_stock_amount
  and status = 1
