mapper.select:
SELECT * FROM sale_activity WHERE 1=1

    AND activity_name LIKE concat("%",concat(#{keyword},"%"))


    AND status = #{status}

ORDER BY id DESC
LIMIT #{offset},#{pageSize}

SELECT count(*) FROM sale_activity WHERE 1=1

    AND activity_name LIKE concat("%",concat(#{keyword},"%"))


    AND status = #{status}

mapper.insert:
INSERT INTO sale_activity (
) VALUES ( null, #{activityName,jdbcType=VARCHAR}, #{activityDesc,jdbcType=LONGVARCHAR}, #{status,jdbcType=INTEGER}, #{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, now(), now() )
SELECT LAST_INSERT_ID();

mapper.update:
UPDATE sale_activity SET modified_time = now()

    ,`activity_name` = #{activityName,jdbcType=VARCHAR}


    ,`start_time` = #{startTime,jdbcType=TIMESTAMP}


    ,`end_time` = #{endTime,jdbcType=TIMESTAMP}


    ,`status` = #{status,jdbcType=INTEGER}


    ,`activity_desc` = #{activityDesc,jdbcType=LONGVARCHAR}

where id = #{id}
