mapper.select:
SELECT * FROM sale_item WHERE 1=1

    AND item_title LIKE concat("%",concat(#{keyword},"%"))


    AND activity_id = #{activityId}


    AND status = #{status}


    AND stock_warm_up = #{stockWarmUp}

ORDER BY id DESC
LIMIT #{offset},#{pageSize}

SELECT count(*) FROM sale_item WHERE 1=1

    AND item_title LIKE concat("%",concat(#{keyword},"%"))


    AND activity_id = #{activityId}


    AND status = #{status}


    AND stock_warm_up = #{stockWarmUp}

mapper.insert:
INSERT INTO sale_item (
) VALUES ( null, #{itemTitle,jdbcType=VARCHAR}, #{itemSubTitle,jdbcType=VARCHAR}, #{itemDesc,jdbcType=LONGVARCHAR}, #{initialStock,jdbcType=INTEGER}, #{availableStock,jdbcType=INTEGER}, #{stockWarmUp,jdbcType=INTEGER}, #{originalPrice,jdbcType=BIGINT}, #{salePrice,jdbcType=BIGINT}, #{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, #{rules,jdbcType=LONGVARCHAR}, #{status,jdbcType=INTEGER}, #{activityId,jdbcType=BIGINT}, now(), now() )
SELECT LAST_INSERT_ID();

mapper.update:
UPDATE sale_item SET modified_time = now()

    ,`item_title` = #{itemTitle,jdbcType=VARCHAR}


    ,`item_sub_title` = #{itemSubTitle,jdbcType=VARCHAR}


    ,`initial_stock` = #{initialStock,jdbcType=INTEGER}


    ,`available_stock` = #{availableStock,jdbcType=INTEGER}


    ,`stock_warm_up` = #{stockWarmUp,jdbcType=INTEGER}


    ,`original_price` = #{originalPrice,jdbcType=BIGINT}


    ,`sale_price` = #{salePrice,jdbcType=BIGINT}


    ,`start_time` = #{startTime,jdbcType=TIMESTAMP}


    ,`end_time` = #{endTime,jdbcType=TIMESTAMP}


    ,`status` = #{status,jdbcType=INTEGER}


    ,`activity_id` = #{activityId,jdbcType=BIGINT}


    ,`item_desc` = #{itemDesc,jdbcType=LONGVARCHAR}


    ,`rules` = #{rules,jdbcType=LONGVARCHAR}


where id = #{id}

UPDATE sale_item
SET modified_time   = now(),
    available_stock = available_stock - #{quantity}
where id = #{itemId}
  and available_stock  >=   #{quantity}

UPDATE sale_item
SET modified_time   = now(),
    available_stock = available_stock + #{quantity}
where id = #{itemId}
  and (available_stock + #{quantity})  <=  initial_stock
